      } else {
        onHide?.();
        setIsReadyToHide(false);
        timer = setTimeout(() => {
          onHidden?.();
        }, tabsPaneTransitionTimeRef.current);
      }
      return () => {
        clearTimeout(timer);
      };
    }, [show]);

    return (
      <Tag className={classes} role="tabpanel" ref={combinedRef} {...props}>
        {children}
      </Tag>
